import React, { Component } from 'react';
import { MDBTooltip } from 'mdb-react-ui-kit';

interface IStatProps {
    stat: number;
}

interface IStatState {}

export abstract class Stat extends React.Component<IStatProps, IStatState> {

    constructor(props: any) {
        super(props);
        this.stat = props.stat;
    }

    stat: number;
    abstract statName: string;
    abstract icon: string;

    public updateStat(s: number) {
        this.stat = s;
    }

    render() {
        return(
            <MDBTooltip wrapperProps={{ color: 'secondary' }} title={this.statName}>
                <div className="stat">
                    <i className={`fa-solid ${this.icon}`}></i>
                    {this.stat}
                </div>
            </MDBTooltip>);
    }
}
