import Entity from "../Entity/Entity";
import React, {Component, createContext, ReactElement, useCallback, useContext, useMemo, useRef, useState} from 'react';
import { MDBContainer } from 'mdb-react-ui-kit';
import Character from '../Models/Character';
import EntityGroupHeader from "../EntityGroupHeader/EntityGroupHeader";
import './EntityGroup.scss';
import {NavbarContext} from "../Navbar/Navbar";

interface IEntityGroupProps {
    entities: Array<Character>
}

export const EntityContext = createContext({
    selectionToggle: (id: string) => {},
    selectedEntity: "",
    currentAction: ""
});
export const EntityActivityContext = createContext({
    activityCallback: (attacking: boolean, healing: boolean) => {}
})

export default function EntityGroup(props: IEntityGroupProps) {

    const [selectedEntity, setSelectedEntity] = useState("")
    const [entities, setEntities] = useState(props.entities.map((c: Character) => renderEntity(c)))
    const [currentAction, setCurrentAction] = useState("selected")

    const selectionToggle = useCallback(
        (entityId: string) => {
            if (entityId !== selectedEntity) {
                setSelectedEntity(entityId)
                return
            }
            setSelectedEntity("")
            return
        }, [selectedEntity, setSelectedEntity]
    )
    const setActionCallback = (attacking: boolean, healing: boolean): void => {
        console.log(`${attacking} +++++ ${healing}`)
        if (attacking) {
            setCurrentAction("attack")
        } else if (healing) {
            setCurrentAction("heal")
        } else {
            setCurrentAction("selected")
        }
    }
    const entityContext = useMemo(
        () => ({
            selectionToggle,
            selectedEntity,
            currentAction
        }),
        [selectionToggle, selectedEntity, currentAction]
    )
    const activityContext = {
        setActionCallback: setActionCallback
    }


    const renderEntity = (c: Character) => {

        const entityProps = {
            id: c.id,
            name: c.name,
            initiative: c.initiative,
            health: c.health,
            ac: c.ac
        }

        return (
            <Entity {...entityProps} />
        )
    }


    return(
        <div className="d-flex justify-content-center entity-group-root">
            <MDBContainer className="entity-group">
                <div className="entity-group-container">
                    <EntityGroupHeader />
                    <div className="entity-group-list">
                        <EntityContext.Provider value={entityContext}>
                            <EntitiyActivityContext value={activityContext}>
                                {entities}
                            </EntitiyActivityContext>
                        </EntityContext.Provider>
                    </div>
                </div>
            </MDBContainer>
        </div>
    )
}
