import { Entity } from "../Entity/Entity";
import React, { Component } from 'react';
import { MDBContainer } from 'mdb-react-ui-kit';
import Character from '../Models/Character';
import EntityGroupHeader from "../EntityGroupHeader/EntityGroupHeader";
import './EntityGroup.scss';

interface IEntityGroupProps {
    entities: Array<Character>
}
interface IEntityGroupState {}

export class EntityGroup extends React.Component<IEntityGroupProps, IEntityGroupState> {

    constructor(props: any) {
        super(props)
        this.entities = props.entities.map(c => this.renderEntity(c));
    }

    entities: Array<Entity> = []

    renderEntity(c: Character) {
        const entityProps = {
            name: c.name,
            initiative: c.initiative,
            health: c.health,
            ac: c.ac
        }
        return (
            <Entity {...entityProps}></Entity>
        )
    }

    render() {
        return(
            <div className="d-flex justify-content-center">
                <MDBContainer className="entity-group">
                    <div className="entity-group-container">
                        <EntityGroupHeader />
                        <div className="entity-group-list">
                            {this.entities.map(entity => this.renderEntity(entity))}
                        </div>
                    </div>
                </MDBContainer>
            </div>
        )
    }
}
