export default function EntityGroup(props) {

    const [deselect, doDeselect] = useState(false)
    const [entities, setEntities] = useState(props.entities.map((c: Character) => renderEntity(c)))
    const onSelectedChange = (id: string): void => {
        console.debug("deselect all but " + id)
        entities
            .filter(e => e.id !== id)
            .forEach(e => doDeselect(!deselect))
    }

    const renderEntity = (c: Character) => {
        const entityProps = {
            id: c.id,
            name: c.name,
            initiative: c.initiative,
            health: c.health,
            ac: c.ac,
            triggerDeselect: onSelectedChange,
            deselectTrigger: deselect
        }
        return (
            <Entity {...entityProps} />
        )
    }



    return(
        <div className="d-flex justify-content-center">
            <MDBContainer className="entity-group">
                <div className="entity-group-container">
                    <EntityGroupHeader />
                    <div className="entity-group-list">
                        {entities}
                    </div>
                </div>
            </MDBContainer>
        </div>
    )


}
