import React, { Component } from 'react';
import { MDBTooltip } from 'mdb-react-ui-kit';
import "./Stat.scss"

interface IStatProps {
    stat: number;
    statName?: string;
    statType?: string;
    icon?: string;
}

interface IStatState {}

