    abstract icon: string;

    public updateStat(s: number) {
        this.stat = s;
    }

    render() {
        return(
            <div className={`stat ${this.statType}`}>
                <i className={`fa-solid ${this.icon}`}></i>
                {this.stat}
            </div>
);
    }
}*/


function Stat(props: IStatProps) {
    return(
        <div className={`stat ${props.statType}`}>
            <i className={`fa-solid ${props.icon}`}></i>
            {props.stat}
        </div>
    )
}

export function Armour(props: IStatProps) {
    const statName = "armour class";
    const icon = "fa-shield";
    const statType = "ac"

    return Stat({
        stat: props.stat,
        statName: statName,
        icon: icon,
        statType: statType
    })
}

export function Health(props: IStatProps) {
    const statName = "Health";
    const icon = "fa-heart";
    const statType = "hp"

    return Stat({
        stat: props.stat,
        statName: statName,
        icon: icon,
        statType: statType
    })
}

export function Initiative(props: IStatProps) {
    const statName = "Initiative";
    const icon = "fa-shield";
    const statType = "inv"

    return Stat({
        stat: props.stat,
        statName: statName,
        icon: icon,
        statType: statType
    })
}
