import React, { Component } from 'react';
import { Armour } from '../Stat/Armour';
import { Initiative } from '../Stat/Initiative';
import { Health } from '../Stat/Health';
import { MDBRow, MDBCol } from 'mdb-react-ui-kit';
import './Entity.scss';

interface IEntityProps {
    name: string;
    initiative: number;
    health: number;
    ac: number;
    level?: number;
}

interface IEntityState {
    selected: boolean;
}

export class Entity extends React.Component<IEntityProps, IEntityState> {

    constructor(props: any) {
        super(props);
        this.name = props.name;
        this.initiative = props.initiative;
        this.health = props.health;
        this.ac = props.ac;
        if(props.level) this.level = props.level;
    }

    name: string;
    initiative: number;
    health: number;
    ac: number;
    level?: number;
    selected: boolean = false;

    onClick() {
        this.setState({
            selected: !this.state.selected
        })
    }

    render() {
        return(
            <div onClick= {this.onClick}>
            <MDBRow className={`entity ${this.state.selected!  ? 'border-selected' : 'border-neutral'}`}>
                <MDBCol md="2" className="stat">
                    <Initiative {...{stat: this.initiative}}></Initiative>
                </MDBCol>
                <MDBCol md="4" className="name">
                            <span>
                                {this.name} {this.level ? `[${this.level}]` : ''}
                            </span>
                </MDBCol>
                <MDBCol md="2" className="stat">
                    <Health {...{stat: this.health}}></Health>
                </MDBCol>
                <MDBCol md="2" className="stat">
                    <Armour {...{stat: this.ac}}></Armour>
                </MDBCol>
            </MDBRow>
            </div>

        )
    }
}
