import React, { Component } from 'react';
import { EntityGroup } from './EntityGroup/EntityGroup'
import data from './entities.json'
import Player from './Models/Player';
import NPC from './Models/NPC';
import Character from './Models/Character';
import './Dnditto.scss'


export default class Dnditto extends Component {

    loadData(): Array<Character> {
        return data.entities.map(entity => {
            if(entity.player) {
                return new Player(
                    entity.name, 
                    entity.initiative, 
                    entity.hp, 
                    entity.ac, 
                    entity.level, 
                    entity.class, 
                    entity.race, 
                    entity.alignment)
            }
            else { 
                return new NPC(entity.name, entity.initiative, entity.hp, entity.ac)
            }
        })
    }

    render() {
        return(
        <div className="dnditto">
            <EntityGroup {...{entities: this.loadData()}}></EntityGroup>
        </div>
        )
    }
}
