import {MDBBtn, MDBContainer, MDBNavbar} from "mdb-react-ui-kit";
import {createContext, useContext, useEffect, useMemo, useState} from "react";
import {EntityActivityContext} from "../EntityGroup/EntityGroup";

export const NavbarContext = createContext({
    attacking: false,
    healing: false,
    // inActionCallback: (): string => { return "" }
})

export default function Navbar() {

    const [attacking, setAttacking] = useState(false)
    const [healing, setHealing] = useState(false)

    const useActionContext = () => {
        const context = useContext(EntityActivityContext)
        if (!context) {
            throw new Error(
                "Activity context error"
            );
        }
        return context
    }

    const { setActionCallback } = useActionContext()

    useEffect(() => {
        console.log(`${attacking} |-|-| ${healing}`)
        setActionCallback(attacking, healing)
    }, [attacking, healing])

    const toggleAttacking = () => {
        if (healing) {
            setHealing(!healing)
            setAttacking(!attacking)
        } else {
            setAttacking(!attacking)
        }
    }

    const toggleHealing = () => {
        if (attacking) {
            setAttacking(!attacking)
            setHealing(!healing)
        } else {
            setHealing(!healing)
        }
    }

    const inActionCallback = (): string => {
        console.log("callback")
        if (attacking) {
            return "attack"
        } else if (healing) {
            return "heal"
        } else {
            return "selected"
        }
    }

    const context = {
        attacking,
        healing
    }

    console.log(`${attacking} --- ${healing}`)

    return (
        <MDBNavbar>
            <MDBContainer tag="form" fluid className="justify-content-end">
                <NavbarContext.Provider value={context}>
                    <MDBBtn outline={!attacking} color="danger" className="me-2" type="button" onClick={() => toggleAttacking()}>
                        Attack
                    </MDBBtn>
                    <MDBBtn outline={!healing} color="success" className="me-2" type="button" onClick={() => toggleHealing()}>
                        Heal
                    </MDBBtn>
                </NavbarContext.Provider>
                <div>
                    <MDBBtn outline color="info" className="me-2" type="button">
                        Next Round
                    </MDBBtn>
                </div>
            </MDBContainer>
        </MDBNavbar>
    )
}
