const emptyFunction = () => {}
export const EntityContext = createContext({});


export default function EntityGroup(props: IEntityGroupProps) {

    const [selectedEntity, setSelectedEntity] = useState("")
    const selectionToggle = useCallback(
        (entityId: string) => {
            console.log(`${entityId} | ${selectedEntity}`)
            if (entityId !== selectedEntity) {
                setSelectedEntity(entityId)
                console.log(selectedEntity)
                updateEntities()
            }
            else {
                setSelectedEntity("")
                updateEntities()
            }
            return
        }, [selectedEntity, setSelectedEntity]
    )
    const selectionContext = useMemo(
        () => ({
            selectionToggle,
            selectedEntity
        }),
        [selectionToggle, selectedEntity]
    )



    const renderEntity = (c: Character) => {

        const entityProps = {
            id: c.id,
            name: c.name,
            initiative: c.initiative,
            health: c.health,
            ac: c.ac,
            selected: c.id === selectedEntity,
            selectedEntity: selectedEntity,
            onClickCallback: selectionToggle
        }

        return (
            <Entity {...entityProps} />
        )
    }

    const [entities, setEntities] = useState(props.entities.map((c: Character) => renderEntity(c)))

    const updateEntities = () => {
        console.debug("updating entities")
        setEntities(props.entities.map((c: Character) => renderEntity(c)))
    }

    return(
        <div className="d-flex justify-content-center">
            <MDBContainer className="entity-group">
                <div className="entity-group-container">
                    <EntityGroupHeader />
                    <div className="entity-group-list">
                        <EntityContext.Provider value={selectionContext}>
                            {entities}
                        </EntityContext.Provider>
                    </div>
                </div>
            </MDBContainer>
        </div>
    )


}
